INSERT INTO public.series (id, "Titulo", descripcion, "Idioma", "Ano", "Episodios", "Estatus", "Valoracion", "Categoria_id", created_at, updated_at, "Portada_file_name", "Portada_content_type", "Portada_file_size", "Portada_updated_at", "Fondo_file_name", "Fondo_content_type", "Fondo_file_size", "Fondo_updated_at") VALUES (10, 'Generador Rex', 'Un virus ha sido esparcido en la atmosfera de la tierra liberando a los Nanites, los cuales infectan a los humanos y se activan al azar causando una transformación del infectada a una criatura con o sin forma humana además de dotarlo de poderes sobrenaturales, para combatir a estos infectados se ha creado una organización conocida como Providencia, destinada a combatir a los infectados, además de buscar una cura. Rex es un humano infectado pero que tiene la habilidad de controlar a voluntad sus poderes, ahora trabajara junto con esta organización para salvar proteger a la humanidad.', 'Español Latino', 2010, 59, 'Activa', 8, 2, '2018-05-03 00:00:00.000000', '2018-07-19 17:45:15.077601', 'MV5BYWJmZTExZWItOWFkZS00ZjY4LWFhOGItNTBjMjQ2MDdkNTkyXkEyXkFqcGdeQXVyNjExODE1MDc_._V1_UX182_CR0_0_182_268_AL_.jpg', 'image/jpeg', 20313, '2018-07-19 17:45:15.054118', 'x720-2rT.jpg', 'image/jpeg', 149040, '2018-07-19 17:45:15.061016');
INSERT INTO public.series (id, "Titulo", descripcion, "Idioma", "Ano", "Episodios", "Estatus", "Valoracion", "Categoria_id", created_at, updated_at, "Portada_file_name", "Portada_content_type", "Portada_file_size", "Portada_updated_at", "Fondo_file_name", "Fondo_content_type", "Fondo_file_size", "Fondo_updated_at") VALUES (11, 'La Mascara Serie Animada', 'El personaje principal Stanley Ipkiss, continua teniendo la máscara, la cual usa para combatir al mal, los poderes de la máscara funcionan tanto en el día como en la noche, junto con la reportera Peggy Brandt quien es el personaje femenino principal. Kellaway junto con la ayuda de su compañero Doyle integran atrapar a la Máscara.', 'Español Latino', 1995, 54, 'Activa', 7, 2, '2018-05-03 00:00:00.000000', '2018-07-19 17:50:28.287134', 'Mascara2.jpg', 'image/jpeg', 31304, '2018-07-19 17:50:28.260129', 'maxresdefault.jpg', 'image/jpeg', 109019, '2018-07-19 17:50:28.264788');
