INSERT INTO public.series (id, "Titulo", descripcion, "Idioma", "Ano", "Episodios", "Estatus", "Valoracion", "Categoria_id", created_at, updated_at, "Portada_file_name", "Portada_content_type", "Portada_file_size", "Portada_updated_at", "Fondo_file_name", "Fondo_content_type", "Fondo_file_size", "Fondo_updated_at") VALUES (95, 'El Oso Yogui', 'El Oso Yogui es un personaje imaginario de dibujos animados. Es un oso antropomorfo y parlante, creado por los estudios de animacion de Hanna-Barbera. Nacio como un personaje de quiebre de Hanna-Barbera y aparecio por primera vez en un capitulo de la serie El Show de Huckleberry Hound, en 1958, emitido por la cadena estadounidense NBC.', 'Español Latino', 1958, 22, 'Activa', 7, 4, '2018-05-03 00:00:00.000000', '2018-07-22 22:55:12.950094', 'The-Yogi-Bear-Show-the-Complete-Series.jpg', 'image/jpeg', 30158, '2018-07-22 22:55:12.922228', 'the_yogi_bear_show.jpg', 'image/jpeg', 55680, '2018-07-22 22:55:12.931772');
